def translate(obj, translations):
    if isinstance(obj, dict):
        return {
            key: translate(value, translations) if key not in {"Message", "Description"} else translations.get(value, value)
            for key, value in obj.items()
        }
    if isinstance(obj, list):
        return [translate(item, translations) for item in obj]
    return obj


def translate_file(src: Path, dst_suffix: str, translations: dict[str, str]) -> None:
    data = json.loads(src.read_text())
    translated = translate(data, translations)
    dst = src.with_name(src.stem + dst_suffix + src.suffix)
    dst.write_text(json.dumps(translated, indent=2, ensure_ascii=False))


def main() -> None:
    apollo = Path("Apollo")
    translate_file(apollo / "lesson1_prelaunch.json", ".ru-ru", translations_lesson1)
    translate_file(apollo / "lesson2_boost.json", ".ru-ru", translations_lesson2)


if __name__ == "__main__":
    main()
